#define Rdn(i) fract(sin(dot(i.xy,vec2(12.9898,78.233)))*43758.5453123)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = gl_FragCoord.xy / iResolution.xy- 0.4; 
    float fonctmountain = uv.x *sin((uv.x* 5.5) + (iTime));
    float mountain = step(fonctmountain + (Rdn(uv)*0.2), uv.y);      
    fragColor = vec4(vec3(mountain),1.0); 
}
