vec3 hash( uvec3 x ) // integer hash from https://www.shadertoy.com/view/XlXcW4
{
    const uint k = 1103515245U;  // GLIB C
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;    
    return vec3(x) / float(0xffffffffU);
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 12.* u / R.y + iTime;

    U *= mat2(1,0,.5,.87);                     // parallelogram frame
    vec3  V = vec3( U, U.y-U.x +3. );          // 3 axial coords
    ivec3 I = ivec3(floor(V)), J;
          I += I.yzx;
          J = ( I % 3 ) / 2;                   // J.xy = hexagon face
    int   c = J.x + 2* J.y;                    // int face id
 /*       J = I % 3;                           // variant:
    int   c = J.x==2 ? 1 : J.y==2 ? 2 : 0;     //   front faces (as above)
 // int   c = J.x==1 ? 1 : J.y==0 ? 2 : 0; */  //   back faces
          I = ( I + ivec3(1,0,0) ) / 3;        // I.xy = hexagon id
    vec3  r = hash(uvec3(I.xy,0));             // rand values per hexagon
//  U = fract(U);                              // local coords
    V = fract( V/2. + .5*vec3( I.y, 1+I.y-I.x, 1+I.x ) );
                                               // --- various hexagons display mode
    O = r.x < .33 ? vec4(V,0)                  // local 3coords
      : r.x < .66 ? vec4(c==1,c==2,c==0,0)     // face ids
      : r.x < .71 ? fract(vec4(I.xy,0,0)/10.)  // tile id
      :             vec4(r.z);                 // random value
    if (r.y > .5) O.xyz *= sin(62.8*V);        // show axis

    U = (V.xy*2.-1.) * mat2( .87,0, -.5,1)/.87;// back to screenspace
    if (r.z > .7) O *= smoothstep( .0, .05, abs(length(U)-1.) );
}
