float sdf(vec3 p)
{
    float m = 1.0;
    for (float i = 0.0; i < 4.0; i++)
    {
        vec4 sphere = vec4(cos(p.y * 4.0 + iTime * i * 2.0 + i * 2.0) * 0.15 + 3.0 * i - 4.0, sin(iTime * 15.0 + i * 5.0) * 0.5 + 1.25 + sin(p.x * 4.0 + iTime * 10.0 + i * 6.0) * 0.15 + 1.2, 6.0, 1.0);
        float distanceSphere = length(p - sphere.xyz) - sphere.w;
        m = min(m, distanceSphere);
    }
    float distancePlane = p.y + cos(p.x + iTime * 2.5) * sin(p.z + iTime * 32.0) * 0.5 + cos(p.z + iTime * 32.0) * 0.5 + 1.75;
    return min(m, distancePlane);
}

vec3 normal(vec3 p)
{
    vec2 delta = vec2(0.01, 0.0);
    float dist = sdf(p);
    return normalize(dist - vec3(
        sdf(p - delta.xyy),
        sdf(p - delta.yxy),
        sdf(p - delta.yyx)
    ));
}

float raymarch(vec3 origin, vec3 direction)
{
    float distanceOrigin = 0.0;

    for (int i = 0; i < 64; i++)
    {
        vec3 point = origin + direction * distanceOrigin;
        float distanceScene = sdf(point);
        distanceOrigin += distanceScene;
        if (distanceOrigin < 0.01 || distanceOrigin > 100.0) break;
    }

    return distanceOrigin;
}

vec3 light(vec3 p, vec3 pos, vec3 col, float s)
{
    vec3 l = normalize(pos - p);
    vec3 n = normal(p);

    float dist = distance(p, pos);
    float att = 1.0 / (dist + 0.15 * dist + 0.15 * dist * dist);

    float diffuse = clamp(dot(l, n), 0.0, 1.0);

    float d = raymarch(p + n * 0.2, l);
    if (d < length(pos - p)) diffuse *= 0.1;

    return col * texture(iChannel0, n.xz).rgb * smoothstep(0.0, 1.0, diffuse) * att * s;
}

vec3 lights(vec3 p)
{
    vec3 position = vec3(0.0, 6.0, 6.0);
    position.xz += vec2(cos(iTime * 2.0), sin(iTime * 2.0)) * 2.0;

    vec3 l = vec3(0.0);

    vec3 color = vec3(0.5);

    color.r = sin(iTime * 2.0 + 50.0) * 0.25 + 1.0;
    color.g = cos(iTime * 2.0 + 150.0) * 0.25 + 1.0;
    color.b = sin(iTime * 2.0 + 200.0) * 0.25 + 1.0;

    l += light(p, position, color, 16.0);
    l += light(p, vec3(-4.0, 4.0, -1.0), vec3(0.15, 0.15, 0.8), 18.0);
    l += light(p, vec3(4.0, 4.0, -1.0), vec3(0.15, 0.8, 0.15), 18.0);

    return l;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0, 2.5, 0.0);
    vec3 rd = normalize(vec3(uv.x - cos(iTime * 29.4) * 0.01, uv.y - sin(iTime * 34.0) * 0.01, 0.5));

    float dist = raymarch(ro, rd);

    vec3 p = ro + rd * dist;
    vec3 diffuse = lights(p);

    col = diffuse;

    fragColor = vec4(col, 1.0);
}
