void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.y;
    float xy_ratio = iResolution.x / iResolution.y;

    vec2 center = vec2(0.5 * xy_ratio, 0.5);
    float r = 0.6;
    float omega = 5.;

    vec3 col = vec3(r * (cos(omega * iTime) + 2.) / 2. - length(uv - center), 0., 0.);


    // Output to screen
    fragColor = vec4(col, 1.0);
}
