void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float adist = distance(uv, vec2(.5, .5));
    float bdist = distance(uv, vec2(1.2, .5));

    float a = (sin((adist-iTime/20.)*10.)+1.)/2.;
    float b = (sin((bdist-iTime/20.)*10.)+1.)/2.;

    vec2 texuv = vec2(a, b);

    vec3 col = texture(iChannel0, texuv).rgb;
    fragColor = vec4(col, 1.0);
}
