vec3 Flower(vec2 st, vec3 color, float numOfPetals, float centeringX, float centeringY){
    st.x -= centeringX;
    st.y -= centeringY;
    float radius = length(st) * 2.0;
    float a = atan(st.y,st.x);
    float f= abs(cos(a*numOfPetals*.5))+.4;
    vec3 temp = vec3(1. - smoothstep(f,radius, .9));
    temp += color;
    return temp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    st.x *= aspect; 
    vec3 color = Flower(st, vec3(sin(iTime/20.),cos(iTime /20.)*0.30,sin(iTime/20.*0.73)), sin(iTime/100.) * 6000., iMouse.x / (iResolution.x / aspect), iMouse.y/iResolution.y);
    fragColor = vec4(color,1.0);
}
