void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 d = D(U);
    float l = ln(U,a.xy,a.zw);
    Q = vec4(1)*smoothstep(0.,1.25,l);
    Q *= 1.-abs(d);
}

#define R iResolution.xy
#define o vec3(1,0,-1)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)

float ln (vec2 p, vec2 a, vec2 b) {
	return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);

    X(Q,U,vec2(1,0));
    X(Q,U,vec2(0,1));
    X(Q,U,vec2(-1,0));
    X(Q,U,vec2(0,-1));

    Q.xy += D(Q.xy).xy;
    Q.zw += D(Q.zw).xy;

    if (length(Q.xy-Q.zw) > 3.5) {
        vec2 m = 0.5*(Q.xy+Q.zw);
        if (length(U-Q.xy) > length(U-Q.zw)) 
        	Q.xy = m;
        else Q.zw = m;
    }
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec2 g = vec2(e.x-w);

    if (iMouse.z>0.) {
        vec4 n = B(vec2(0));
    	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
    }

    if (iFrame < 1) {
    	Q = vec4(.11,0,.11,1)*R.xyxy;
    	vec4 n = vec4(.89,0,.89,1)*R.xyxy;
        if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
    }
}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

vec4 T(vec2 U) {
	U -= .5*D(U).xy;
	U -= .5*D(U).xy;
    return C(U);
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = T(U);
    vec4 
        n = T(U+o.yx),
        e = T(U+o.xy),
        s = T(U+o.yz),
        w = T(U+o.zy),
        m = 0.25*(n+e+s+w);
    float div = 0.25*(n.y+e.x-s.y-w.x);
    Q.xy -= 0.25*vec2(e.z-w.z,n.z-s.z);
	Q.z = m.z-div;
    if (length(U-vec2(.1,0.5)*R) < 5.) Q.xy = vec2(.3,0);
    if (length(U-vec2(.9,0.5)*R) < 5.) Q.xy = vec2(-.3,0);
    if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.) Q.xy *= 0.;
	if (iFrame < 1) Q = vec4(0,0,0,0);
}

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        n = C(U+o.yx),
        e = C(U+o.xy),
        s = C(U+o.yz),
        w = C(U+o.zy);
    Q = 0.25*(n+e+s+w);
}
