void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        //polar coordinates
    vec2 pc=vec2(
        atan(uv.y, uv.x),
        distance(vec2(.5, .5), uv)
    );

    vec3 col;
    if (floor(mod(pc.y*100.0, 5.0)) == 0.0){
    	col = vec3(0.4, 0.6, .8);
    }

    if (floor(pc.x + sin(iTime)) == 0.0){
    	col.rg += .84;
        col.b += .6;
    }

    if (floor(mod(pc.y*iTime*100.0, 15.0)) == 0.0){
    	col.b += .84;
    }

    if (floor(mod(pc.y*iTime*800.0, 15.0)) == 0.0){
    	col.g += .35;
    }


    if (floor(mod(pc.y*10.0, 10.0)) == floor(mod(iTime*3.0, 10.0)) ){
        col.g += .15;
    }
    if (floor(pc.y * 100.0* abs(sin(iTime))) == floor(100.0*distance(vec2(.5, .5), uv)) ){
        col.r += .25;
        col.g += .25;
        col.b += .25;
    }

    if (floor(pc.y * 5.0* abs(tan(iTime))) == floor(100.0*distance(vec2(.5, .5), uv)) ){
        col.r += .6;
        col.g += .4;
        col.b += .25;
    }

    //if (floor(col.x * 10.0* (tan(iTime))) == floor(100.0*distance(vec2(.75, .25), uv)) ){
    //    col.r += 1.00;
    //    col.g -= .25;
    //    col.b -= .25;
    //}

    if (dot(col, col) < .2 ){
        col.b += 2.0*dot(col, col);
    }

    //if (dot(pc, col.rb) < .01 ){
        //col.r += dot(pc, col.rb);
        //col.g += dot(pc, col.gb);
    //}




    // Output to screen
    fragColor = vec4(col,1.0);
}
