#define PI 3.1415

bool circle( in vec2 ctr, in vec2 uv, in float rad)
{
    return distance(ctr, uv) < rad;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 ctr = vec2(.5, .5);

    float rad = .6 * (cos(iTime) + 1.0);

    vec3 col;

    ctr.x = cos(uv.x*iTime*75.0);
    ctr.y = cos(uv.y*iTime*100.0) + cos(uv.x*iTime*10.0);


    //if (circle(ctr, uv, rad*(cos(iTime) + 1.0))){
    if (circle(ctr, uv, rad)){

    	col.g = sin(iTime) - .6;
        col.b = cos(iTime) + sin(iTime) - .4;

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
