void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // move mouse to compare
    float delt = iResolution.x*0.5;
    if (iMouse.z > 0.5) {
        delt = iMouse.x;
    }

    // black line in the center
    if (abs(fragCoord.x-delt) < 1.0) {
        fragColor = vec4(0);
        return;
    }

    vec2 uv = (fragCoord*2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(1);

    for (int i = 0 ; i < 3 ; i++) {
        float theta = float(i) / 3.0 * 2.0 * acos(-1.0) + iTime*0.2;
        vec2 center = vec2(cos(theta), sin(theta))*0.5;

        vec3 c = vec3(0);
        c[i] = 1.0;

        col = mix(col, c, smoothstep(0.8, 0.7, length(uv-center)));
    }

    fragColor = vec4(col, 1);

    // apply gamma correction
    if (fragCoord.x < delt) {
        fragColor.rgb = pow(fragColor.rgb, vec3(1.0/2.2));
    }
}
