void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;

    for(int i=1;i<=6;++i)
    {  
   		p=abs(p*1.5)-0.8;
        float b=float(i)*iTime+iMouse.x*0.01;
        float c=cos(b);
   		float s=sin(b);
        p*=mat2(c,s,-s,c);
    }

    vec2 axis=1.-smoothstep(0.01,0.05,abs(p));
    vec2 color=mix(p,vec2(1),axis.x+axis.y);  
    fragColor=vec4(color,0.4,1.);

}
