#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)-.5;
	uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);//vec3(1.-fract(iTime/20.)-uv.x*uv.y);

    float signal = max(
       abs (cos(uv.x*20.*(.5*sin(iTime/2.)+1.)*PI+iTime*4.)+.3),
       abs (sin(uv.y*20.*(.5*sin(iTime/2.)+1.)*PI-iTime*3.)+.3)
    );

    vec3 interestingColor = vec3(
        0.,
        signal*.3, 
        signal*.4
    );

    col = mix(col, vec3(interestingColor), signal);


    // Output to screen
    fragColor = vec4(col,1.0);
}
