void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float wave = +(sin(iTime-uv.x*5.)/(20.0));


    if(uv.x < .95 && uv.x > .05)
    {
    if(uv.y>.9 + wave)
        fragColor = vec4(0,0,0,0);



    else if(uv.y>.655 + wave){    
        fragColor = vec4(.7-(wave*5.),0,0,1);
    }
    else if(uv.y>.345 + wave){
        fragColor = vec4(1.-(wave*5.),1.-(wave*5.),1.-(wave*5.),1);}
    else if(uv.y>.1 + wave)
        fragColor=vec4(0,0,.7-(wave*5.),1);
        }

}
