void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 sx = vec2(1.0, 0.0) / iResolution.xy;
    vec2 sy = vec2(0.0, 1.0) / iResolution.xy;

    vec4 tex11 = texture( iChannel0, uv - sx - sy);
    vec4 tex12 = texture( iChannel0, uv - sy);
    vec4 tex13 = texture( iChannel0, uv + sx - sy);
    vec4 tex21 = texture( iChannel0, uv - sx);
    vec4 tex22 = texture( iChannel0, uv);
    vec4 tex23 = texture( iChannel0, uv + sx);
    vec4 tex31 = texture( iChannel0, uv - sx + sy);
    vec4 tex32 = texture( iChannel0, uv + sy);
    vec4 tex33 = texture( iChannel0, uv + sx + sy);

    float val = (tex11.r > 0.0 || tex12.r > 0.0 || tex13.r > 0.0 
                 || tex21.r > 0.0 || tex23.r > 0.0 
                 || tex31.r > 0.0 || tex32.r > 0.0 || tex33.r > 0.0) && tex22.r < 0.5 ? 1.0 : 0.0;

    fragColor = vec4(val, val, val, val);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture( iChannel0, uv );

    float val = tex.r > 0.5 || tex.g > 0.5 || tex.b > 0.5 ? 1.0 : 0.0;

    fragColor = vec4(val, val, val, 1.0);
}
