void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.xyxy-.5, d=p, t, c;
    p.z += iTime;p.y += sin(iTime);
    for(float i=1.; i>0.; i-=.01)
    {
        t = abs(mod(p, 8.)-4.);
        c = texture(iChannel0, t.zxy-2.5);
        float x = min(t.y, length(t.xz)-1.2+c.x);
        f = c*i;
        if(x<.01) break;
        p -= d*x;
     }
}
