//Rendering
mat2 ro (float a) {
	float s = sin(a),c = cos(a);
    return mat2(c,-s,s,c);
}
float map (vec3 p) {
	return dist(p, s3d(iChannel1,p));
}
vec3 norm (vec3 p) {
    vec2 e = vec2 (.0001,0.);
    return normalize(vec3(
        map(p+e.xyy) - map(p-e.xyy),
        map(p+e.yxy) - map(p-e.yxy),
        map(p+e.yyx) - map(p-e.yyx)
    ));
}
void mainImage( out vec4 Q, in vec2 U )
{ R = iResolution.xy;

   	vec3 p = vec3(.5*R/N,-.5*R.x/N);
 	vec3 d = normalize(vec3(2.*(U-0.5*R)/R.y,2));

 	p.xz *= ro(.5*sin(.5*iTime));
 	d.xz *= ro(.5*sin(.5*iTime));

 	for (int i = 0; i < 50; i++) {
        p += map(p)*d;
 	}
 	vec3 n = norm(p);
 	Q = (0.5+0.5*dot(n,vec3(0,0,-1)))*(0.5+0.5*sin(log(1.+30.*length(s3d(iChannel0,s3d(iChannel1,p).xyz).xyz))*vec4(1,2,3,4)));
 	if (length(p)>R.y)
 	Q = 0.5+2.*texture(iChannel0,U/R).zzzz
 	 + vec4(.2)*smoothstep(1.,0.,dist(d3(U),texture(iChannel1,U/R)));
    //Q.xyz = d3(U)/vec3(R/N,N*N);
}

//Fluid
#define s 0.16666666666
void mainImage( out vec4 Q, in vec2 U )
{	R = iResolution.xy;
    vec3 o = d3(U);
    Q = s3d1(iChannel0,o);
    vec4
        r = s3d1(iChannel0,o+vec3( 1,0,0)),
        l = s3d1(iChannel0,o+vec3(-1,0,0)),
        u = s3d1(iChannel0,o+vec3(0, 1,0)),
        d = s3d1(iChannel0,o+vec3(0,-1,0)),
        f = s3d1(iChannel0,o+vec3(0,0, 1)),
        b = s3d1(iChannel0,o+vec3(0,0,-1)),
        m = s*(u+d+l+r+f+b);

   Q.x += s*(r.w-l.w); 
   Q.y += s*(u.w-d.w); 
   Q.z += s*(f.w-b.w);
   Q.w = m.w + s*(r.x-l.x+u.y-d.y+f.z-b.z);
   Q.w -= .003*smoothstep(1.,0.,dist(o,s3d(iChannel1,o)));
   if (length(o.xy-0.5*vec2(R/N)) < 2.) Q.xy = 0.3*vec2(cos(.1*iTime),sin(.1*iTime));
     if (iFrame < 1){
         Q = vec4(0);

     }
}

// 3D utilities 
#define N 7.
vec2 R;
//  [ [0,R/N]; [0,N*N]  ] < -- > [0,R]
vec2 d2 (vec3 U) {
    U = clamp(U,vec3(1),vec3(R/N,N*N));
	return U.xy+vec2(mod(U.z,N),floor(U.z/N))*R/N;
}
vec3 d3 (vec2 u) {
    vec2 o = floor(u/R*N);
	return vec3(mod(u,R/N),o.x+o.y*N);
}
vec4 s3d (sampler2D T,vec3 U) {
    vec3 U0 = vec3 (U.xy,floor(U.z)),
         U1 = vec3 (U.xy, ceil(U.z));
    vec4 o = mix(
        texture(T,d2(U0)/R),
        texture(T,d2(U1)/R),
        fract(U.z)
    );
    return o;
}
vec4 s3d1 (sampler2D T, vec3 U) {
    U=U-s3d(T,U).xyz;
    vec4 s = s3d(T,U);
    if (U.x < 4. || R.x/N-U.y < 4. ||
        U.y < 4. || R.y/N-U.y < 4. ||
       	U.z < 1. || U.z > 1.+N*N) s.xyz*=0.5;
    return s;
}
float dist (vec3 U, vec4 A) {
	return length(U-A.xyz)-A.w;
}

//Particles
void swap (vec3 U, inout vec4 A, vec4 B) {if (dist(U,B) < dist(U,A)) A = B;}
void mainImage( out vec4 Q, in vec2 U )
{	R = iResolution.xy;
    Q = texture(iChannel1,U/R);
    vec3 o = d3(U);
    swap(o,Q,s3d(iChannel1,o+vec3(1,0,0)));
    swap(o,Q,s3d(iChannel1,o-vec3(1,0,0)));
    swap(o,Q,s3d(iChannel1,o+vec3(0,1,0)));
    swap(o,Q,s3d(iChannel1,o-vec3(0,1,0)));
    swap(o,Q,s3d(iChannel1,o+vec3(0,0,1)));
    swap(o,Q,s3d(iChannel1,o-vec3(0,0,1)));
 /*
 	swap(o,Q,s3d(iChannel1,o+vec3(1,1,1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(1,1,-1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(1,-1,1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(-1,1,1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(1,-1,-1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(-1,-1,1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(-1,1,-1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(-1,-1,-1)));
  */
 	Q.xyz += .5*s3d(iChannel0,Q.xyz).xyz;
 	Q.xyz = clamp(Q.xyz,vec3(0),vec3(R/N,N*N));
   if (iFrame < 1) {
       Q = vec4(round(o/3.)*3.,.5);
   }
}

