    swap(o,Q,s3d(iChannel1,o+vec3(0,0,1)));
    swap(o,Q,s3d(iChannel1,o-vec3(0,0,1)));
 /*
 	swap(o,Q,s3d(iChannel1,o+vec3(1,1,1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(1,1,-1)));
 	swap(o,Q,s3d(iChannel1,o+vec3(1,-1,1)));
