// --- 153 chars
void mainImage( out vec4 O, vec2 u )
{  
    vec2 f = normalize(texture(iChannel0,u/30./iResolution.y).xy-.5), // vector field
         p = 2.*fract(u/8.)-1.;           // local pos in cell
    float v = dot(p,f);                   // distance to vector
    O = vec4(.1/abs(v));                  // draw vector
}

