void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float angle = iTime*2.0;
    float s = sin(angle);
    float c = cos(angle);

    mat2 rotationMatrix = mat2( c, s,
                               	-s,  c);

    vec2 pivot = vec2( 0.5, 0.5);


    // alternative that doesn't warp, scaled to the aspect ratio if. Thanks FabriceNeyret2!
    //uv = rotationMatrix * ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    uv = rotationMatrix * (uv - pivot) + pivot;

    vec3 col = texture(iChannel0, uv).xyz;

    fragColor = vec4(col,1.0);
}
