/*

==================== Sorting series ====================

https://www.shadertoy.com/view/tsByzt - odd-even sorting network (aka parallel bubble sort).
https://www.shadertoy.com/view/tsBczt - odd-even horizonta-vertical experiment.
https://www.shadertoy.com/view/tdByzd - (2^N)-1 comb sorting network.
https://www.shadertoy.com/view/tsScWG - Leonardo comb sorting network.

Press 'R' to Reset.
Press 'D' or hold left mouse button to see how much data is sorted.

Buffer A - initial state of data to sort.
Buffer B - test control variables (todo), currently just previous resolution.
Buffer C - the sorting network.
Image - display dataset number, current data state, current sorting state.

*/

int D(vec2 p, float n) {
    int i=int(p.y), b=int(exp2(floor(30.-p.x-n*3.)));
    i = ( p.x<0.||p.x>3.? 0:
    i==5? 972980223: i==4? 690407533: i==3? 704642687: i==2? 696556137:i==1? 972881535: 0 )/b;
 	return i-i/2*2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 muv = iMouse.xy/iResolution.xy;
    if (iMouse.z>0. && muv.x+muv.y<uv.x+uv.y || iMouse.z<=0. && uv.x+uv.y>1.+.3*sin(iTime*.5) || texelFetch(iChannel1,ivec2(KEY_D,2),0).r>0.)
        fragColor = lum(texelFetch(iChannel0,ivec2(fragCoord),0)) < lum(texelFetch(iChannel0,ivec2(fragCoord)-ivec2(1,0),0)) ? vec4(0.) : vec4(1.);
    else
	    fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);

    // display dataset number
    ivec4 conf = config(iResolution, iFrame);
    vec2 i = fragCoord / 10.;
    if ((i.x-=1.)<3. && i.y<7.) { fragColor += vec4(D(i,float(conf.w))); }
}

//#define BW
#define KEY_R 82
#define KEY_D 68

vec4 get(int index, sampler2D chan, vec3 res) {
    ivec2 p;
    p.x = index%int(res.x);
    p.y = index/int(res.x);
    return texelFetch(chan,p,0);
}

float lum(vec4 c)
{
    return dot(c.rgb*vec3(0.3,0.59,0.11),vec3(1.));
}

vec4 bw(vec4 col)
{
    col.r = (col.r+col.g+col.b)/3.;
    return col.rrra;
}

vec4 init(sampler2D chan, vec2 uv)
{
    vec4 col = texture(chan, uv);
#ifdef BW
    return bw(col);
#else
    return col;
#endif
}

bool isSorted(vec4 a, vec4 b)
{
    return lum(a) <= lum(b);
}

vec4 compareAndSwap(int i, int j, int isSecond, int size, sampler2D chan, vec3 res)
{
    vec4 a = get(i, chan, res);
	if (j<0 || j>=size) return a;
    vec4 b = get(j, chan, res);

    if (isSecond!=0)
    {
        // note we have to keep the order of comparison, or we will lose pixels!
        // as we can have less(a,b)==less(b,a), both false.
        if (isSorted(b,a)) return a;
        return b;
    }
    if (isSorted(a,b)) return a;
    return b;
}

ivec4 config(vec3 res, int globFrame)
{
    int size = int(res.x)*int(res.y);

    int len = int(log2(float(size)));
    len = len*len+60;

    int frame = globFrame%len;

	int set = globFrame/len%9;

    return ivec4(size,len,frame,set);
}

int xy2i(vec2 pos, vec3 res)
{
    return int(pos.x)+int(res.x)*int(pos.y);
}

/////////////////////////////////////////////////////////////////////////
// common

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec4 conf = config(iResolution,iFrame);
    int index = xy2i(fragCoord, iResolution);
    vec2 uv = fragCoord/iResolution.xy;

    if (conf.w==0)
    {
        // horizontal split, only 4 values
        fragColor = vec4(float(int(fragCoord.x)*4/int(iResolution.x))/4.);
    }
    else if (conf.w==1)
    {
        // reversed
        fragColor = vec4(float(conf.x-index-1)/float(conf.x));
    }
    else if (conf.w==2)
    {
        // color gradient
        fragColor = vec4(1.-uv.y, 1.-uv.x, uv.y, 0.);
    }
    else if (conf.w==3)
    {
        // all sorted, but last element moved to front.
        fragColor = vec4(float((index+conf.x-1)%conf.x)/float(conf.x));
    }
    else if (conf.w==4)
    {
        // even are sorted, odd are reversed
        if (index%2==0)
            fragColor = vec4(0.,float(index)/float(conf.x),0.,0.);
        else
            fragColor = vec4(float(conf.x-index-1)/float(conf.x),0.,0.,0.);
    }
    else if (conf.w==5)
    {
        // London texture
        fragColor = vec4(init(iChannel0, uv).rgb,0.);
    }
    else if (conf.w==6)
    {
        // Alphabet texture
        fragColor = vec4(init(iChannel1, uv).rgb,0.);
    }
    else if (conf.w==7)
    {
        // Blue noise texture
        fragColor = vec4(init(iChannel2, uv).rgb,0.);
    }
    else
    {
        // RGB noise texture
        fragColor = vec4(init(iChannel3, uv).rgb,0.);
    }
    fragColor.a = 0.; // frame number
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(iResolution,0.);
}

// common

vec4 get(int index)
{
    return get(index, iChannel0, iResolution);
}

vec4 compareAndSwap(int i, int j, int isSecond, int size)
{
    return compareAndSwap(i, j, isSecond, size, iChannel0, iResolution);
}

/////////////////////////////////////////////////////////////////////////
// sorting

int leonardo(int i)
{
    if(i<2) return 1;
    int a=1;
    int b=1;
    for (;i>1;i--)
    {
        int t = b;
        b += a+1;
		a = t;
    }
    return b;
}

#define max_step_power 24
#define large_cycle 11
#define small_cycle 5

vec4 update(int i, int size, int frame)
{
    // All distances are odd, because we select odd-even pairs.
    int isSecond = (i+frame)%2;
    int k = frame/large_cycle - frame%small_cycle;
    int dist = leonardo(max_step_power-k);
    int j = i + dist*(1 - isSecond*2);

    return compareAndSwap(i, j, isSecond, size);
}

/////////////////////////////////////////////////////////////////////////
// common

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    ivec4 conf = config(iResolution,iFrame);

    if (conf.z>=0 && conf.z<60 || get(0,iChannel1,iResolution).xy != iResolution.xy || texelFetch(iChannel3,ivec2(KEY_R,0),0).r>0.)
    {
        fragColor = texture(iChannel2,uv);
    }
    else
    {
        int index = xy2i(fragCoord, iResolution);
        int frame = int(texelFetch(iChannel0,ivec2(fragCoord),0).a*256.);
        fragColor = update(index, int(iResolution.x)*int(iResolution.y), frame);
    	fragColor.a = float(frame+1)/256.;
    }
}
