// 2pi = 6.28318;
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

// background
vec3 bckg ( in float x, in float y )
{
    return pal( x, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );
}

// amplitude, period, position, width, x, y, col 
vec3 sine( in float amp, in float prd, in float psn, in float wid, in float x, in float y, in vec3 col )
{
    // exact position of the sine wave
    float spos = sin(x* prd + iTime) * amp + psn;

    // render the area above the sine wave as white
    if(abs(spos - y) <= wid)
        return vec3(1.0);
    else
    	return col;
}

// amplitude, period, position, width, x, y, col 
vec3 csine( in float amp, in float prd, in float psn, in float wid, in float x, in float y, in vec3 col )
{
    // exact position of the sine wave
    float spos = cos(x* prd + iTime) * amp + psn;

    // render the area above the sine wave as white
    if(abs(spos - y) <= wid)
        return vec3(0.0);
    else
    	return col;
}

// amplitude, period, position, width, x, y, col 
vec3 tang( in float amp, in float prd, in float psn, in float wid, in float x, in float y, in vec3 col )
{
    // exact position of the sine wave
    float spos = tan(x* prd + iTime) * amp + psn;

    // render the area above the sine wave as white
    if(abs(spos - y) <= wid)
        return vec3(0.0);
    else
    	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    vec3 col = pal( p.x, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );

    for(float i = 0.1; i <= 1.0; i += 0.1)
        col = sine(i*0.5, i*10.0, 0.5, i*0.015, p.x, p.y, col);


    /*for(float i = 0.1; i <= 1.0; i += 0.1)
        col = csine(i*0.5, i*10.0, 0.5, i*0.015, p.x, p.y, col);*/

    for(float i = 0.1; i <= 1.0; i += 0.1)
        col = tang(i*0.5, i*10.0, 0.5, i*0.015, p.x, p.y, col);

    // Output to screen
    fragColor = vec4(col,1.0);
}
