const int maxCount = 99;
const float maxRadius = 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 C = (fragCoord*2.0 - iResolution.xy) / iResolution.x;
    C *= 2.0;
    C.x -= 0.5;

    float a = C.x;
    float b = C.y;
    float r = length(C);
    int count = 0;

    float k = iMouse.x/iResolution.x * 2.0 + 2.0;

    for(; count < maxCount && r < maxRadius; count++)
    {
        //float an = a*a - b*b + C.x;
        //float bn = 2.0 * a * b + C.y;
        //a = an;
        //b = bn;

        float theta = atan(b, a);        
        a = pow(r, k) * cos(theta * k) + C.x;
        b = pow(r, k) * sin(theta * k) + C.y;
        r = sqrt(a*a + b*b);
    }

    float v = log(float(count+1)) / log(10.0) / 2.0;
    fragColor = vec4(vec3(v), 1.0);
    //fragColor = vec4(vec3(log(float(count)) / 8.0, float(count) / 100.0, log(float(count)) / 8.0), 1.0);
}
