vec4 fire_gen(float noise, float _min, float _max, float gap)
{
    vec4 fire = vec4(0.);
    for( float i = _min; i < _max; i += gap )
    {
        float cut = step(noise,i);
        fire += vec4(0.8,0.3,0.0,gap)*(1.-cut);
    }
    return fire;
}

vec2 area = vec2(5.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q = floor(fragCoord/area)*area/iResolution.y;

    vec4 tex = texture(iChannel1,uv);

    float scroll = iTime*0.3;
    float noise = texture(iChannel0,q/6.+vec2(0.,-scroll)).r;

    vec4 fire = fire_gen(noise*(1.-q.y), .2,1.,0.1);
    fragColor = mix(tex,fire,fire.a);
}
