//-------------------------------------------------------------------------------
vec4 g_posVel1				= vec4(0.0);
vec4 g_posVel2				= vec4(0.0);
vec4 g_data1				= vec4(0.0);
vec4 g_data2				= vec4(0.0);
vec4 g_special1				= vec4(0.0);
vec4 g_special2				= vec4(0.0);
//-------------------------------------------------------------------------------

//-------------------------------------------------------------------------------
// utils
//-------------------------------------------------------------------------------
vec4 loadValue( in ivec2 re )
{
    return texelFetch( iChannel0, re, 0 );
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

vec2 opSmoothUnionV2( vec2 d1, vec2 d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2.x-d1.x)/k, 0.0, 1.0 );
    float d = mix( d2.x, d1.x, h ) - k*h*(1.0-h); 
    return vec2( d, ( h < 0.5 ) ? d2.y : d1.y);
}

vec2 opSmoothSubtractionV2( vec2 d1, vec2 d2, float k )
{
    float h = clamp( 0.5 - 0.5*(d2.x+d1.x)/k, 0.0, 1.0 );
    float d = mix( d2.x, -d1.x, h ) + k*h*(1.0-h);
    return vec2( d, ( h < 0.5 ) ? d2.y : d1.y);
}

mat3 rotX(float a)
{
    return mat3(1,0,0, 0,cos(a),-sin(a), 0,sin(a),cos(a));
}

mat3 rotY(float a)
{
    return mat3(cos(a),0,sin(a), 0,1,0, -sin(a),0,cos(a));
}

mat3 rotZ(float a)
{
    return mat3(cos(a),-sin(a),0, sin(a),cos(a),0, 0,0,1);
}

vec3 attachToParent(vec3 parentPos, vec3 translation, mat3 rotation)
{
    vec3 pos = parentPos;
    pos -= translation;
    pos *= rotation;

    return pos;
}


//-------------------------------------------------------------------------------
// main code
//-------------------------------------------------------------------------------
vec2 buildCharacter(vec3 p, vec3 posOffset, vec3 posOffset2, vec4 special, float headOffset, float inputWalk, float breathSpeed, float headMix01, float matBody, float matEyes, float matEyesSockets)
{
    inputWalk += 0.5 * PI;

    headOffset *= 0.5 + 0.5 * (1.0 - special.x);

    // rotations
    mat3 rotEyes 		= rotX(0.05 * PI * sin(2.3 * iTime)) * rotY(0.2 * PI * sin(iTime));
    mat3 rotHandL 		= rotZ( 0.125 * PI) * rotX(0.06 * PI * sin(5.0 * iTime));
    mat3 rotHandR 		= rotZ(-0.125 * PI) * rotX(0.06 * PI * sin(0.5 * PI + 5.0 * iTime));
    mat3 rotWalk  		= rotY(0.3 * PI * sin(inputWalk));
    mat3 rotWalkHead  	= rotY(0.1 * PI * sin(inputWalk)) * rotZ(0.25 * sin((0.4 * PI + inputWalk)));
    mat3 rotHole  		= rotX(0.5 * PI );

	float angleHands = 0.25 * PI;
    vec2 cHands = vec2(sin(angleHands),cos(angleHands));    

    // transformations
    vec3 posRoot 		= p - posOffset;
    vec3 posRoot2 		= p - posOffset - posOffset2;
    vec3 posBody 		= attachToParent(posRoot	, vec3(0.0, 0.3, 0.0)			, rotWalk);
    vec3 posHead 		= attachToParent(posRoot2	, vec3(0.0, headOffset + 0.08 * sin(PI + iTime * breathSpeed), -0.2)	, rotWalkHead);
    vec3 posHandL		= attachToParent(posHead	, vec3( 1.0, -0.6, -0.2)		, rotHandL);
    vec3 posHandR		= attachToParent(posHead	, vec3(-1.0, -0.6, -0.2)		, rotHandR);
    vec3 posEyeL 		= attachToParent(posHead	, vec3( 0.5, 0.4, -0.7)			, rotEyes);
    vec3 posEyeR 		= attachToParent(posHead	, vec3(-0.5, 0.4, -1.0)			, rotEyes);
    vec3 posEyePupilL 	= attachToParent(posEyeL	, vec3( 0.0, 0.0, -EYE_RADIUS_L), MX_ID);
    vec3 posEyePupilR 	= attachToParent(posEyeR	, vec3( 0.0, 0.0, -EYE_RADIUS_R), MX_ID);
    vec3 posHole 		= attachToParent(posHead	, vec3( 0.3 * sin(0.5 * breathSpeed * iTime), -0.5,-0.6 )		, rotHole);

    // shapes
    //float fLegs 		= sdCappedTorus		(posBody, vec2(sin(LEGS_ANGLE),cos(LEGS_ANGLE)), 1.2, 0.1); 
    float fLegs 		= sdCappedTorus		(posBody, vec2(sin(LEGS_ANGLE),cos(LEGS_ANGLE)), 0.8, 0.1); 
    float fHeadV1 		= sdSphere			(posHead, 1.0 + 0.05 * sin(iTime * breathSpeed));
    float fHeadV2 		= sdRoundBox		(posHead, vec3(0.6 + 0.05 * sin(iTime * breathSpeed), 1.1, 0.7), 0.1);
    float fHead			= mix(fHeadV1, fHeadV2, headMix01);
    float fHandL 		= sdCappedTorus		(posHandL, cHands, 0.6, 0.2 );
    float fHandR 		= sdCappedTorus		(posHandR, cHands, 0.6, 0.2 );
    float fEyeSocketL 	= sdSphere			(posEyeL, 1.2 * EYE_RADIUS_L);
    float fEyeSocketR 	= sdSphere			(posEyeR, 1.2 * EYE_RADIUS_R);
    float fEyeL 		= sdSphere			(posEyeL, EYE_RADIUS_L);
    float fEyeR 		= sdSphere			(posEyeR, EYE_RADIUS_R);
    float fEyePupilL 	= sdSphere			(posEyePupilL, 0.1);
    float fEyePupilR 	= sdSphere			(posEyePupilR, 0.06);
    float fHole 		= sdVerticalCapsule	(posHole, 1.0, -0.02 + special.y * (0.3 + 0.03 * sin(4.0 * breathSpeed * iTime)));

    // distances + materials
    vec2 dLegs 			= vec2(fLegs		, matBody);
    vec2 dHead 			= vec2(fHead		, matBody);
    vec2 dHandL			= vec2(fHandL		, matBody);
    vec2 dHandR			= vec2(fHandR		, matBody);
    vec2 dEyeSocketL 	= vec2(fEyeSocketL	, matEyesSockets);
    vec2 dEyeSocketR 	= vec2(fEyeSocketR	, matEyesSockets);
    vec2 dEyeL 			= vec2(fEyeL		, matEyes);
    vec2 dEyeR 			= vec2(fEyeR		, matEyes);
    vec2 dEyePupilL 	= vec2(fEyePupilL	, MAT_EYES_DOTS);
    vec2 dEyePupilR 	= vec2(fEyePupilR	, MAT_EYES_DOTS);
    vec2 dHole 			= vec2(fHole		, matEyesSockets);

    // build character
    vec2 dCharacter = opSmoothUnionV2		(dLegs			, dHead			, 1.8);
    dCharacter 		= opSmoothUnionV2		(dHandL			, dCharacter	, 0.15);
    dCharacter 		= opSmoothUnionV2		(dHandR			, dCharacter	, 0.15);
    dCharacter 		= opSmoothSubtractionV2	(dEyeSocketL	, dCharacter	, 0.05);
    dCharacter 		= opSmoothSubtractionV2	(dEyeSocketR	, dCharacter	, 0.05);
    dCharacter 		= opSmoothUnionV2		(dEyeL			, dCharacter	, 0.05);
    dCharacter 		= opSmoothUnionV2		(dEyeR			, dCharacter	, 0.05);
    dCharacter 		= opSmoothUnionV2		(dEyePupilL		, dCharacter	, 0.0);
    dCharacter 		= opSmoothUnionV2		(dEyePupilR		, dCharacter	, 0.0);
    dCharacter 		= opSmoothSubtractionV2	(dHole			, dCharacter	, 0.05);

    vec2 dFloorCutoff = vec2(p.y - 0.01, MAT_FLOOR);
    dCharacter 		= opSmoothSubtractionV2	(dFloorCutoff	, dCharacter	, 0.05);

    return dCharacter;
}

//-------------------------------------------------------------------------------
vec2 getDist(vec3 p)
{
    // input data
    vec3 pos1 = vec3(0.0);
    vec3 pos2 = vec3(0.0);
    pos1.xz = g_posVel1.xy;
    pos2.xz = g_posVel2.xy;

    vec3 posDelayed1 = vec3(0.0);
    vec3 posDelayed2 = vec3(0.0);
    posDelayed1.xz = g_data1.xy;
    posDelayed2.xz = g_data2.xy;

    // build characters
    vec2 dCharacter1 = buildCharacter(p, pos1 + START_POS_PL_1, 
                                      posDelayed1.xyz - pos1.xyz,
                                      g_special1,
                                      2.3,
                                      g_data1.w,
                                      BREATH_SPEED, 
                                      0.0, MAT_BODY_1, MAT_EYES_1, MAT_EYES_SOCKETS_1);

    vec2 dCharacter2 = buildCharacter(p, pos2 + START_POS_PL_2, 
                                      posDelayed2.xyz - pos2.xyz,
                                      g_special2,
                                      2.1,
                                      g_data2.w, 
                                      1.2 * BREATH_SPEED, 
                                      0.8, MAT_BODY_2, MAT_EYES_2, MAT_EYES_SOCKETS_2);

    // build scene
    vec2 dFinal = vec2(100000.0);
    dFinal = opSmoothUnionV2(dFinal, dCharacter1, 0.3);
    dFinal = opSmoothUnionV2(dFinal, dCharacter2, 0.3);

    vec2 dFloor = vec2(p.y, MAT_FLOOR);
    dFinal = opSmoothUnionV2(dFinal, dFloor, 0.0);//0.3);

    return dFinal;
}

//-------------------------------------------------------------------------------
vec2 rayMarch(vec3 ro, vec3 rd)
{
    vec2 dist 	= vec2(0.0);
    vec2 d 		= vec2(0.0);
    vec3 pos 	= vec3(0.0);

    for(int i=0; i<MAX_STEPS; i++)
    {
        pos = ro + dist.x * rd;
        d = getDist(pos);
		dist.x += d.x;
        dist.y = d.y;
		if (dist.x<MIN_DIST)
            break;

        if (dist.x>MAX_DIST)
            return vec2(0.0, 0.0);
    }

    return dist;
}

//-------------------------------------------------------------------------------
float shadowMarch(vec3 ro, vec3 rd, vec3 lightPos)
{
    float dist = 0.0;


    float res = 1.0;

    float power = 2.0;

    for(int i=0; i<MAX_STEPS; i++)
    {
        vec3 pos = ro + dist * rd;
        float d = getDist(pos).x;
        if (d < 0.01)
            return 0.0;

		dist += d;

        res = min(res, power * d / dist);

		if (dist<MIN_DIST || dist>MAX_DIST)
            break;
    }

    return res;
}

//-------------------------------------------------------------------------------
vec3 getNormal(vec3 p)
{
    float d = getDist(p).x;

    vec2 e = vec2(0.001, 0.0);
    //vec2 e = vec2(0.1, 0.0);

    vec3 n = d - vec3(getDist(p - e.xyy).x,
                  	  getDist(p - e.yxy).x,
                      getDist(p - e.yyx).x);

    return normalize(n);
}


//-------------------------------------------------------------------------------
// From:
// Physically-based SDF by romainguy
// https://www.shadertoy.com/view/XlKSDR
//-------------------------------------------------------------------------------
vec3 Tonemap_ACES(const vec3 x)
{
    // Narkowicz 2015, "ACES Filmic Tone Mapping Curve"
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

//-------------------------------------------------------------------------------
vec3 OECF_sRGBFast(const vec3 linear)
{
    return pow(linear, vec3(1.0 / 2.2));
}
//-------------------------------------------------------------------------------


//-------------------------------------------------------------------------------
// Lighting taken from:
// Ray Marching: Part 2 by jlfwong
// https://www.shadertoy.com/view/lt33z7
//-------------------------------------------------------------------------------
/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 N, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity)
{
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    float shadow = shadowMarch(p + N * 0.1, L, lightPos);
    dotLN *= shadow;

    if (dotLN < 0.0)
    {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0)
    {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    vec3 col =  lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));

    return col;
}

//-------------------------------------------------------------------------------
/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 N, vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye)
{
    const vec3 ambientLight = GLIGHT_MUL * 0.1 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          5.0,
                          0.0 * cos(iTime));
    vec3 light1Intensity = GLIGHT_MUL * 1.3 * vec3(1.0, 0.9, 0.8);
    //vec3 light1Intensity = vec3(0.);

    color += phongContribForLight(N, k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(4.0 * sin(0.37 * iTime),
                          7.0 + 2.0 * cos(0.37 * iTime),
                          -1.0);
    vec3 light2Intensity = GLIGHT_MUL * 1.3 * vec3(0.6, 0.7, 1.0);
    //vec3 light2Intensity = vec3(0.);

    color += phongContribForLight(N, k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    

    return color;
}
//-------------------------------------------------------------------------------



//-------------------------------------------------------------------------------
// shade
//-------------------------------------------------------------------------------
vec3 shade(vec3 ro, vec3 p, vec3 normal, float mat, float bgAnimSpeed, vec3 fogColor, float dist)
{
    vec3 col = vec3(0.0);

	// material parameters
	vec3 colAmbient 	= vec3(0.2, 0.2, 0.2);
	vec3 colDiffuse 	= 0.6 * vec3(1.0, 0.85, 0.8);
	vec3 colSpecular 	= vec3(1.0, 1.0, 1.0);
	float shininess 	= 100.0;
	float rimStrength 	= 0.4;
	float rimWidth 		= 0.8;
	vec3 rimColor 		= vec3 ((0.7 + 0.2 * sin(0.3 + 1.86 * iTime * bgAnimSpeed)), 
								(0.8 + 0.2 * sin(0.6 + 1.92 * iTime * bgAnimSpeed)), 
								(1.0 + 0.2 * sin(0.2 + 1.64 * iTime * bgAnimSpeed)));

	// floor
	if ( abs(mat - MAT_FLOOR) < 0.1)
	{
		colDiffuse = 0.5 * vec3(1.0, 0.85, 0.8);
		//float param = clamp(pow(sin(3.0*p.x + 3.0*p.z), 6.0), 0.0, 1.0);
		//float param = clamp(pow(sin(0.07*dot(p, p + sin(10.0 * iTime) * vec3(1.0, 2.3, 0.3))), 6.0), 0.0, 1.0);
		//colDiffuse = vec3(0.6, 0.5, 0.1 + 0.4 * param);

		rimStrength = 0.5;
	}
	// body 1
	else if ( abs(mat - MAT_BODY_1) < 0.1)
	{
		vec3 pos = p;
		pos.xz -= g_posVel1.xy;
		pos.y -= 0.8 * (1.0 - g_special1.x);
		float colorVal = 3.0 * sin(15.0*pos.y + 10.0*pos.x);
		//float colorVal = 3.0 * sin(15.0*cos(pos.y) + 30.0*sin(pos.x));
		colDiffuse = vec3(0.8, 0.5 * clamp(0.1 + colorVal, 0.0, 1.0), 0.4);

		//shininess = 2.0;
		//colSpecular *= 0.12;
	}
	// body 2
	else if ( abs(mat - MAT_BODY_2) < 0.1)
	{
		vec3 pos = p;
		pos.xz -= g_posVel2.xy;
		pos.y -= 0.8 * (1.0 - g_special2.x);

		float colorVal = 3.0 * sin(15.0*pos.y + 10.0*pos.x);
		//float colorVal = 3.0 * sin(15.0*cos(pos.y) + 30.0*sin(pos.x));
		colDiffuse = vec3(clamp(0.5 + colorVal, 0.0, 1.0), 0.5, 0.3);

		//shininess = 2.0;
		//colSpecular *= 0.12;
	}
	// eyes 1
	else if( abs(mat - MAT_EYES_1) < 0.1)
	{
		colDiffuse = vec3(1.0, 0.9, 0.8);
		rimStrength = 0.1;
	}
	// eyes 2
	else if( abs(mat - MAT_EYES_2) < 0.1)
	{
		colDiffuse = vec3(0.9, 1.0, 0.6);
		rimStrength = 0.1;
	}
	// eyes pupils
	else if( abs(mat - MAT_EYES_DOTS) < 0.1)
	{
		colDiffuse = vec3(0.0, 0.0, 0.0);
		rimStrength = 0.0;
	}
	// eyes sockets 1
	else if ( abs(mat - MAT_EYES_SOCKETS_1) < 0.1)
	{
		colAmbient = vec3(0.0, 0.0, 0.0);
		colDiffuse = vec3(0.0, 0.0, 0.0);
		colSpecular = vec3(0.0, 0.0, 0.0);

		rimColor = 0.7 * vec3(0.9, 0.1, 0.5);
		rimWidth = 0.7;
		rimStrength = 1.4;
	}
	// eyes sockets 2
	else if ( abs(mat - MAT_EYES_SOCKETS_2) < 0.1)
	{
		colAmbient = vec3(0.0, 0.0, 0.0);
		colDiffuse = vec3(0.0, 0.0, 0.0);
		colSpecular = vec3(0.0, 0.0, 0.0);

		rimColor = 0.7 * vec3(0.1, 0.7, 0.4);
		rimWidth = 0.7;
		rimStrength = 0.8;
	}

	// lighting
	col = phongIllumination(normal, colAmbient, colDiffuse, colSpecular, shininess, p, ro);

	// rim light
#if USE_RIM_LIGHT
	vec3 viewDir = normalize( ro - p);
	float vDotN = dot(normal, viewDir);
	float rim = smoothstep(1.0 - rimWidth, 1.0, 1.0 - vDotN);
	col += 0.6 * rim * rimStrength * rimColor;
#endif

    // fog
    col = mix(col, fogColor, 1.0 - exp2(-0.0001 * dist * dist * dist));

	// debug - materials
    //col = 0.1 * vec3(mat);

    // debug - rim
    //col = vec3(rim);

    // debug - normals
    //col = normal;

    return col;

}

//-------------------------------------------------------------------------------
// main
//-------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    // camera pos
    vec3 ro = vec3(0,1.7,0);

    // ray direction
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    // read input
    g_posVel1	= loadValue( txPosVel1  );
    g_posVel2	= loadValue( txPosVel2  );
    g_data1		= loadValue( txData1  	);
    g_data2		= loadValue( txData2  	);
    g_special1	= loadValue( txSpecial1 );
    g_special2	= loadValue( txSpecial2	);

    // distance to the scene
    vec2 result = rayMarch(ro, rd);
    float d = result.x;
    float mat = result.y;

    float bgAnimSpeed = 0.3;

    vec3 fogColor = vec3((0.7 + 0.1 * sin(0.3 + 1.36 * iTime * bgAnimSpeed)), 
                         (0.8 + 0.1 * sin(0.6 + 1.22 * iTime * bgAnimSpeed)), 
                         (0.7 + 0.1 * sin(0.2 + 1.54 * iTime * bgAnimSpeed)));

    fogColor.x -= 0.7 * uv.y;

    fogColor *= GLIGHT_MUL;

    if (d <= 0.01f)
    {
        col = fogColor;
    }
    else
    {
        vec3 p = ro + rd * d;

	    vec3 normal = getNormal(p);

        col = shade(ro, p, normal, mat, bgAnimSpeed, fogColor, d);
    }

    // contrast
    col *= 0.9;
    col = pow(col, vec3(1.3));

#if USE_TONE_MAPPING
    // tone mapping
    col = Tonemap_ACES(col);
#endif

    // gamma compression
    col = OECF_sRGBFast(col);

    // vignette
    col *= 1.0 - dot(uv,uv)*0.4;

    // output to screen
    fragColor = vec4(col,1.0);
}

