vec4 loadValue( in ivec2 re )
{
    return texelFetch( iChannel0, re, 0 );
}

void storeValue( in ivec2 re, in vec4 va, inout vec4 fragColor, in ivec2 p )
{
    fragColor = (p==re) ? va : fragColor;
}

void storeValue( in ivec4 re, in vec4 va, inout vec4 fragColor, in ivec2 p )
{
    fragColor = ( p.x>=re.x && p.y>=re.y && p.x<=re.z && p.y<=re.w ) ? va : fragColor;
}

void handlePlayerInput(inout vec4 posVel, inout vec4 data, inout vec4 special, int keyL, int keyR, int keyF, int keyB, int keySpecial1, int keySpecial2)
{
    // unpack data
    vec2 pos = posVel.xy;
    vec2 vel = posVel.zw;

    vec2 deltaTemp 	= vec2(0.0);

    float moveRight 	= texelFetch( iChannel1, ivec2(keyR			, 0), 0 ).x;
    float moveLeft  	= texelFetch( iChannel1, ivec2(keyL			, 0), 0 ).x;
    float moveForw  	= texelFetch( iChannel1, ivec2(keyF			, 0), 0 ).x;
    float moveBack  	= texelFetch( iChannel1, ivec2(keyB			, 0), 0 ).x;
    float inputSpecial1	= texelFetch( iChannel1, ivec2(keySpecial1	, 0), 0 ).x;
    float inputSpecial2	= texelFetch( iChannel1, ivec2(keySpecial2	, 0), 0 ).x;

    float inputSum = 0.0;
    inputSum += moveRight;
    inputSum += moveLeft;
    inputSum += moveForw;
    inputSum += moveBack;
    float wasInput = abs(inputSum);


    deltaTemp.x = steeringStrength * (moveRight - moveLeft);
    deltaTemp.y = steeringStrength * (moveForw  - moveBack);


    if (AUTO_DANCE)
    {
        if (DANCE_ALL_THE_TIME || (special.w > AUTO_DANCE_TIMER * 75.0))
        {
            // dancing
            deltaTemp.xy += 0.03 * vec2(cos(2.3 * iTime), sin(3.9 * iTime));
            deltaTemp = clamp(deltaTemp, vec2(-1.0), vec2(1.0));
        }
    }    

    vel = mix(vel, deltaTemp, vec2(0.05));

    pos += vel;

    data.xy = mix(data.xy, pos, vec2(0.08));

    float lenVel = length(vel);

    // anim timer
    data.z += 1.8 * lenVel;

    // anim timer (smooth)
    data.w = mix(data.w, data.z, 0.1);

    // crouch
    special.x = mix(special.x, inputSpecial1, 0.03);

    // sing
    special.y = mix(special.y, inputSpecial2, 0.12);

    // timer without input
    if (wasInput > 0.01)
    {
    	special.w = 0.0;
    }
    else
    {
    	special.w += 1.0;
    }

    // debug
    //special.y = (special.w > AUTO_DANCE_TIMER * 75.0) ? 1.0 : 0.0;


    // pack data
    posVel.xy = pos;
    posVel.zw = vel;
}

void snapToCenter(inout vec2 pos, inout vec2 vel)
{
    vec2 center = vec2(0.0, 15.0);

    vec2 vToCenter = center - pos;
    float distFromCenter = length(vToCenter);
    vec2 vDirToCenter = normalize(vToCenter);

    float wallVal = 0.005 * pow(max(distFromCenter-11.0, 0.0), 4.0);
    vel += wallVal * vDirToCenter;

}

void resolveCollision(inout vec4 posVel1, inout vec4 posVel2)
{
    // unpack data
    vec2 pos1 = posVel1.xy;
    vec2 vel1 = posVel1.zw;
    vec2 pos2 = posVel2.xy;
    vec2 vel2 = posVel2.zw;

    pos1 += START_POS_PL_1.xz;
    pos2 += START_POS_PL_2.xz;


    vec2 vBetween = pos2 - pos1;
    float dist = abs(length(vBetween));

    vec2 vDir = normalize(vBetween);

    float minDist = 2.8;

    if (dist < minDist)
    {
        float correction = 0.5 * (dist - minDist);
        vel2 += 0.008 * vDir;
        vel1 -= 0.008 * vDir;
    }

    snapToCenter(pos1, vel1);
    snapToCenter(pos2, vel2);

    pos1 -= START_POS_PL_1.xz;
    pos2 -= START_POS_PL_2.xz;

    // pack data
    posVel1.xy = pos1;
    posVel1.zw = vel1;
    posVel2.xy = pos2;
    posVel2.zw = vel2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // don't compute outside of the data area
    if( fragCoord.x > 7.0 || fragCoord.y > 1.0 )
        discard;

    ivec2 ipx = ivec2(fragCoord-0.5);


    vec4 posVel1	= loadValue( txPosVel1  );
    vec4 posVel2	= loadValue( txPosVel2  );
    vec4 data1		= loadValue( txData1  	);
    vec4 data2		= loadValue( txData2  	);
    vec4 special1	= loadValue( txSpecial1 );
    vec4 special2	= loadValue( txSpecial2 );


    handlePlayerInput(posVel1, data1, special1, 
                                KEY_1_LEFT, 
                                KEY_1_RIGHT, 
                                KEY_1_FORW,
                                KEY_1_BACK,
                     			KEY_1_SPEC_A,
                     			KEY_1_SPEC_B);

    handlePlayerInput(posVel2, data2, special2,
                                KEY_2_LEFT, 
                                KEY_2_RIGHT, 
                                KEY_2_FORW,
                                KEY_2_BACK,
                     			KEY_2_SPEC_A,
                     			KEY_2_SPEC_B);

    resolveCollision(posVel1, posVel2);

    fragColor = vec4(0.);

    storeValue( txPosVel1, 	posVel1,   	fragColor, ipx );
    storeValue( txPosVel2, 	posVel2,	fragColor, ipx );
    storeValue( txData1, 	data1,   	fragColor, ipx );
    storeValue( txData2, 	data2,   	fragColor, ipx );
    storeValue( txSpecial1, special1,  	fragColor, ipx );
    storeValue( txSpecial2, special2,  	fragColor, ipx );
}

