void mainImage( out vec4 o, in vec2 f ) {
    vec3 w,p;
    for(int i=0;i<32;++i)p=vec3((f*2.-iResolution.xy)/iResolution.y,1)*w+iTime,w+=length(cos(p.xx)+sin(p.yz)*mainSound( in int samp,iTime).x)*.3;
    o=vec4(w*.1,1);
}

vec2 mainSound( in int samp,float t) {
    int i=int(t*8000.);
    return vec2(float((i>>4|(i*(bool(i>>4&128)?i>>10&i>>9:0)))&255)/512.-1.);
}
