float smin( float a, float b )
{
    // exponential
    const float k = 5.8;
    return -log(exp(-k*a)+exp(-k*b))/k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;


    uv.x = dot(uv,uv);

    uv.xy += iTime*0.1;

    vec2 grid = abs(0.5-fract(uv*10.0-0.5))*0.4;
    float d1 = smoothstep(0.0,0.1,grid.x);
    float d2 = smoothstep(0.0,0.1,grid.y);
    d1 = 1.0-smin(d1,d2);

    d1 = pow(d1,4.0);
    vec3 col = vec3(0.3,1.0,0.3)*d1;

	fragColor = vec4(col, 1.0);
}
