// normalized rainbow color 0..1
float LinearRampUp(float value, float stepIndex, float stepCount)
{
    float rampStep = 1.0 / stepCount;
    return clamp((value - stepIndex * rampStep) * stepCount, 0.0, 1.0);
}

float LinearRampDown(float value, float stepIndex, float stepCount)
{
    return 1.0 - LinearRampUp(value, stepIndex, stepCount);
}

vec3 RainbowColor(float x)
{
    float r = LinearRampUp( x, 4.0, 6.0 ) + LinearRampDown( x, 1.0, 6.0 );
    float g = LinearRampUp( x, 0.0, 6.0 ) * LinearRampDown( x, 3.0, 6.0 );
    float b = LinearRampUp( x, 2.0, 6.0 ) * LinearRampDown( x, 5.0, 6.0 );
    return vec3(r,g,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(RainbowColor(uv.x),1.0);
}
