vec2 location = vec2(0.5 ,0.5);
float width = 0.2;
float height = 0.2;
vec3 bkCol = vec3(1.0, 1.0, 1.0);
vec3 squareCol = vec3(1.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Fix Ratio
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;
    location.y *= aspectRatio;

    // Draw Square
    float ws = length(location.x - uv.x);
    float ht = length(location.y - uv.y);

    if(ws <= width && ht <= height){
        fragColor = vec4(squareCol, 1.0);  
    }else{
     	fragColor = vec4(bkCol, 1.0);   
    }
}
