#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel0,(U)/R)
vec2 h(vec2 p)//Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

mat2 ro (float a) {
	float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}
void mainImage( out vec4 Q, in vec2 U )
{
   Q = A(U);

    vec4 
        n = A((U)+vec2(0,1)),
        e = A((U)+vec2(1,0)),
        s = A((U)-vec2(0,1)),
        w = A((U)-vec2(1,0)),
        m = 0.25*(n+e+s+w);
    float div = 0.25*(n.y-s.y+e.x-w.x);	
    float curl = 0.25*(n.x-s.x-e.y+w.y);	

    Q.xy *= ro (.03+curl*div);
    Q.xy = mix(Q.xy,m.xy,.5);
    if (length(Q.xy)>0.) Q.xy = normalize(Q.xy);

    if (iFrame < 1||(iMouse.z>0.&&length(U-iMouse.xy)<20.)||length(Q.xy)<1e-4) {
    	Q = vec4(normalize(h(U)*2.-1.),0,1);
    }
}

#define X 4.5
float time;
float ln (vec2 p, vec2 a, vec2 d) {
    float r = clamp(dot(p-a,d)/dot(d,d),0.,1.);
    return 0.8*length(p-a-d*r);
}
void mainImage( out vec4 Q, vec2 U )
{
    vec4 a = A(U);
    Q =  vec4(1);
    for (int x = -3; x <= 3; x++)
    for (int y = -3; y <= 3; y++) {
        vec2 V = floor(U/X+0.5+vec2(x,y))*X;
        V += X*h(V)-.5;
        vec4 a;
        a = A(V);
        #define L 4.
        for (float i = 0.; i < L; i++) {
            Q += exp(-ln(U,V,1.5*X*a.xy/L));
            V += 1.5*X*a.xy/L;
            a = A(V);
        }   Q  += exp(-ln(U,V,1.5*X*a.xy/L));
    }
    Q  /= 8.;
}
