void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = 6.0*(fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 z = vec3(p, 1.0);
    vec2 b = (0.5*iResolution.xy - iMouse.xy)/iResolution.y;
    float a = 2.0 + b.x;

    z /= dot(z.xy,z.xy);
    z.x += mod(iTime*0.25, a);
    z.y += b.y;

    for (int i = 0; i < 18; ++i) {
        z /= dot(z.xy,z.xy);
        z.xy = abs(mod(z.xy - a*0.5, a) - a*0.5);
    }

    float col = z.y*iResolution.y*0.1 / z.z;

    fragColor = vec4(col, col, col, 1.0);
}
