vec2 zoom(vec2 xy)
{
    mat2 translate=mat2(abs(sin(iTime)),0,
                        0,abs(sin(iTime)));
    xy*=translate;
    return xy;
}

vec2 zoom1(vec2 xy,float size)
{
    mat2 translate=mat2(1./size,0,
                        0,1./size);
    xy*=translate;
    return xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord/iResolution.xy;
    xy-=0.5;
  //  xy=zoom1(xy,2.);
    xy=zoom(xy);
    vec4 texColor=texture(iChannel0,xy+0.5);

    fragColor=texColor;
}
