void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = sin(iTime*5.f*(2.*fragCoord-0.5-iResolution.xy)/iResolution.xy);
    vec3 bk = vec3(0.0,0.0,0.0);    
    bk += vec3(uv,0.0);
    // Output to screen
    fragColor = vec4(bk,1.0);
}
