vec2 swapUV(vec2 uv, vec2 fragCoord, vec2 pos, float size) {


    if (fragCoord.x > pos.x && fragCoord.x < pos.x + size && fragCoord.y > pos.y && fragCoord.y < pos.y + size) {

      uv.x -= 0.4;
    }

    return uv;
}

vec2 centerInWindow(in vec2 fragCoord) {
    return (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 spiraledTex(vec2 coord, vec2 pos, float radius) {
	float len = distance(coord, pos);
    if (len < radius) {
        return pos + vec2(sin(len), cos(len)) * radius;
    }
    return coord;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float rate = 1.0;
    float baseRadius = 100.0;
    float spiralDist = 0.3;

    vec4 col = texture(iChannel0, fragCoord.xy / iResolution.xy);

    for (float i = 0.0; i < 1.0; i += spiralDist) {

        vec2 center = vec2(iResolution.x * abs(cos(iTime / rate)), iResolution.y * i);

        float dist = abs(baseRadius * sin(iTime * i / rate));


    	vec2 pos = spiraledTex(fragCoord, center , dist);

		vec4 tex = texture(iChannel0, pos / iResolution.xy);
         fragColor = tex;
        if (pos != fragCoord) {
           break;
        }
    }
}
