// variant of https://www.shadertoy.com/view/3ds3RH

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = fract( (u+u - R)/R.y )*2.-1. ;
    O = texture(iChannel0, .5 -vec2(.1*iTime,0)
                +.55 * length( U )  /* 3/pi: .477: 4/2pi: .637 */
                * cos( abs( fract( atan(U.y,U.x)*.637 - .5 ) - .5 )/.637 +vec2(0,11)) ); }
