float elfld_hash( float n ) { return fract(sin(n)*3.5453123); }
  float elfld_noise( in vec3 x )
  {
      vec3 p = floor(x);
      vec3 f = fract(x);
      f = f*f*(3.0-2.0*f);

      float n = p.x + p.y*157.0 + 113.0*p.z;
      float e1 = elfld_hash(n+0.0);
      return mix(mix(mix( e1, elfld_hash(n+1.0),f.x),
                       mix( elfld_hash(n+157.0), elfld_hash(n+158.0),f.x),f.y),
                  mix(mix( elfld_hash(n+113.0), elfld_hash(n+114.0),f.x),
                       mix( elfld_hash(n+270.0), elfld_hash(n+271.0),f.x),f.y),f.z);
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 position = ( fragCoord.xy / iResolution.xy);
  vec2 uv = position.xy * 20.0;
  float a = abs(elfld_noise(vec3(uv+iTime*3.14,sin(iTime)))-elfld_noise(vec3(uv+iTime,sin(iTime+3.))));
  //(0, .5-pow(a, .2)/2., 1.-pow(a, .2), 1) //blue
  fragColor = vec4((1.-pow(a, .2))*.1, (1.-pow(a, .2))*.9, (1.-pow(a, .2))*.1, 1);
}
