/*
 Formula by IQ, with added translation (qd) and elongation/compression (qc)
*/
vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d, in float qc, in float qd){
    return a + b * sin(2.0 * 3.14159265359 * (c * t * qc + d  + qd));
}

/*
   z^2+c, scaled for 16/9
*/
float mb(in vec2 ri, in float time){
    float r = (ri.x-0.5)*4.5-0.5;
    float i = (ri.y-0.5)*3.0;
    float zr = 0.0;
    float zi = 0.0;
    float max = time;
    for(float k=0.0;k<max;k++){
        float t = zr*zr - zi*zi + r;
        zi = 2.0*zr*zi + i;
        zr = t;
        if(zr*zr+zi*zi > 4.0){
            return k/max;
        }
    }
    return 1.0;
}


/*
 from http://www.flong.com/texts/code/shapers_circ/
*/
float trafo(float x, float a){
  float y = 0.0;
  if (x<=a){
    y = sqrt(a*a - (x-a)*(x-a));
  } else {
    y = 1.0 - sqrt((1.0-a)*(1.-a) - (x-a)*(x-a));
  }
  return y;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Coloring
    float t2 = trafo(uv.x, 0.5)*trafo(uv.y,0.5);
    vec3 col = pal(t2, vec3(0.5),vec3(0.25),vec3(0.5),vec3(0.2,0.3,0.5),3.0,iTime);
    float u = 0.1+mod(iTime*0.25,0.9);
    u = u*u*200.0;
    if(u >= 100.0){
       u = 200.0-u;
    }
    col = col*0.3 + col*vec3(mb(uv,u));

    // Output to screen
    fragColor = vec4(col,1.0);
}
