float Gear(float angle, float armCount, float rotationSpeed, float armLength, float gearSize, float direction)
{
    //gear          		 						       armCount                   rotationSpeed   armLength     gearSize
    float  radius = smoothstep(-1.0 ,  1.0,  cos(angle * armCount + (direction * iTime * rotationSpeed))) * armLength  + gearSize;

    return radius;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2((0.5 * ratio) , 0.5);
    float dist = distance(shapePos , uv) * 2.50;

    uv -= shapePos;

    float angle = atan(uv.y, uv.x);

    float gear = Gear(angle, 8.0, 2.0, 0.2, 0.4, 1.0);

    float spikes =  abs(cos(angle * 75.0 + iTime) * sin(angle * 7.0 + iTime)) * 0.5 + 0.1;

    float value =  step(gear + spikes * clamp(sin(iTime * 1.0)*5.0, 0.1, 10.0), dist);

    // Output to screen
    fragColor = vec4(value + 
                     vec4(//colors
                         vec3(clamp(sin(iTime), 0.0, 1.0),
                              clamp(sin(iTime), 0.0, 0.5), 
                             clamp(sin(iTime), 0.0, 0.5)) 
                          , 1.0));
}
