// How NOT to multply by 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float f = 1.0;

#if 0    
    // what you should do
    f = f*0.5;
#else    
    // what you don't want to do
    f = uintBitsToFloat(   (floatBitsToUint(f)&0x807fffffU)|
                        ((((floatBitsToUint(f)>>23)&255U)-1U)<<23));
#endif

    fragColor = vec4(f,f,f,1.0);
}
