// golfing https://shadertoy.com/view/tsfSDN


void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U =  ( u+u - R ) / R.y
             * mat2(1,-2,1,2) / 1.5;
    U.y += iTime;
    vec4 T;
    for (float t=0.,h=t,z; t<9.; t+= .1) 
        T = texture(iChannel0, U/8.+vec2(t,-t)/5e2),
        z = 9.*T.r + t,
        z > h ? h = z, O = T : O;
}
