//Programmed live during a Shadertoy Tutorial Session at Villanova University
float STEPS=64.;
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 c = fragCoord/iResolution.xy;
    c=2.*c-1.;
    c.x*=iResolution.x/iResolution.y;
    vec2 m = 2.*iMouse.xy/iResolution.xy-1.;
   	vec2 z = c;
    float i;
    for(i=0.;i<STEPS;i++){
    	vec2 tz=m+vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y);
        z=tz;
        if(length(z)>2.){
        	break;
        }
    }
    float ic=5.*i/STEPS;
    vec3 col=vec3(cos(1.-ic),cos(2.-ic),cos(3.-ic));
    //alternate color scheme
    //vec3 col=vec3(cos(1.-ic)+1.,cos(2.-ic)+1.,cos(3.-ic)+1.)/2.;
    fragColor = vec4(col,1.0);
}
