float Gear(float angle, float armCount, float rotationSpeed, float armLength, float gearSize, float direction)
{
    float  radius = smoothstep(-1.0 ,  1.0,  cos(angle * armCount + (direction * iTime * rotationSpeed))) * armLength  + gearSize;

    return radius;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Colors in vec3
    vec3 red = vec3(0.8 ,0.0 ,0.0);
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 yellow = vec3(0.9, 0.9, 0.3);
    vec3 blue = vec3(0.0, 0.2, 1.0);
    vec3 black = vec3(0.0, 0.0, 0.0);


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2((0.5 * ratio), 0.5);
    float dist = distance(shapePos , uv) * 4.50;


    uv -= shapePos;
    float angle = atan(uv.y, uv.x);

    float gear = Gear(angle, 12.0, 20.0, 0.2, 0.5, -1.0);



     vec3 test = mix(black, red, Gear(angle, 12.0, 20.0, 0.2, 0.5, -1.0));
    test = mix(test, white, Gear(angle, 8.0, 10.0, 2.0, 0.1, 1.0));

    float value =  step(gear , dist);
    vec3 val = value * vec3(1.0, 1.0, 1.0);

    // Output to screen
    fragColor = vec4(test  , 1.0);
}
