#define scale 3.

float Random(vec2 p) {
	vec3 a = fract(vec3(p.xyx) * vec3(213.897, 653.453, 253.098));
    a += dot(a, a.yzx + 79.76);
    return fract((a.x + a.y) * a.z);
}

vec2 Random2(vec2 p){
    return vec2(Random(p),Random(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    vec2 mouse = (2.*iMouse.xy - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);

    vec2 grid = fract(scale*uv)-0.5;
    mouse = (scale*mouse)-0.5;

    vec2 gridId = floor(uv*scale);

    vec2 rnd = Random2(gridId)*0.7/scale;
    float angle = 3.14*  (0.5*(iTime* (length(rnd)))-1.);
	mat2 rot = mat2(cos(angle),-sin(angle),sin(angle),cos(angle));

	grid+=rnd*rot;
    grid/=rnd*4.;

    float dist = clamp(0.,2., 2.-(length(mouse-(gridId-rnd*rot))));
    if(iMouse.z<0.1){
        dist=1.;
    }

    fragColor += vec4(smoothstep(0.2,0.0,length(grid))*dist); 


}
