////////////////////////////////////////////////////////////////////////////////
//
// Compound of Five Tetrahedra as Excavated Dodecahedron
//
// Matthew Arcus, 2020
//
// Take an icosahedral kaleidoscope (using precalculated values), and
// take slices through the fundamental region with planes through the
// vertices of a dodecahedron. The result is a great range of shapes -
// stellations, facetings and other more irregular but pretty shapes.
//
// Here we see the compound of five tetrahedra as a chiral excavation.
//
// <mouse>: rotation
// <up>/<down>: move in/out
// c: clip at x=0
// r: autorotation
// 
//
////////////////////////////////////////////////////////////////////////////////

bool dorotate = true;
bool doclip = false;
const float PI	= 3.14159265359;
int NFOLDS = 5;
bool alert = false;

bool dosubtract = false;

void assert(bool test) {
  if (!test) alert = true;
}

// Face plane normals, dot(A,B) = -cos(PI/P) etc.
vec3 A = vec3(1,0,0);
vec3 B = vec3(-0.5,0.866025,0);
vec3 C = vec3(-0.809017,-0.467086,0.356822);

// Unit length vertices of fundamental trihedron
vec3 a = vec3(0.309017,0.178411,0.934172);
vec3 b = vec3(0,0.607062,0.794654);
vec3 c = vec3(0,0,1);

vec3 vertices[20] =
  vec3[](vec3(0,0,1), // 0

         vec3(0.57735,0.333333,0.745356), // 0.713644
         vec3(0,-0.666667,0.745356), // 0.713644
         vec3(-0.57735,0.333333,0.745356), // 0.713644

         vec3(0.356822,0.872678,0.333333), // 1.1547
         vec3(0.934172,-0.127322,0.333333), // 1.1547
         vec3(0.57735,-0.745356,0.333333), // 1.1547
         vec3(-0.577351,-0.745356,0.333333), // 1.1547
         vec3(-0.934172,-0.127322,0.333333), // 1.1547
         vec3(-0.356822,0.872678,0.333333), // 1.1547

         vec3(0.57735,0.745356,-0.333334), // 1.63299
         vec3(0.934172,0.127322,-0.333334), // 1.63299
         vec3(0.356822,-0.872678,-0.333334), // 1.63299
         vec3(-0.356822,-0.872678,-0.333334), // 1.63299
         vec3(-0.934172,0.127322,-0.333334), // 1.63299
         vec3(-0.57735,0.745356,-0.333334), // 1.63299

         vec3(0,0.666666,-0.745356), // 1.86834
         vec3(0.57735,-0.333334,-0.745356), // 1.86834
         vec3(-0.57735,-0.333334,-0.745356), // 1.86834
         vec3(0,0,-1) // 2
         );

// Could use an ivec3 and count different types of flip
vec3 fold(vec3 pos, out int flips) {
  // We want to know the number of actual reflections made
  // so keep track of that in flips.
  flips = 0;
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*A;
    }
    k = dot(pos,B);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*B;
    }
    k = dot(pos,C);
    if (k < 0.0) {
      flips += 1;
      pos -= 2.0*k*C;
    }
  }
  return pos;
}

// Perpendicular distance from p to segment ab
float segment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

vec4 makeplane(vec3 p, vec3 q, vec3 r) {
  // dot(vec4(s,1),makeplane(p,q,r)) gives distance from plane
  vec3 n = cross(q-p,r-p);
  n = normalize(n);
  float k = -dot(p,n);
  // Fix so that away from origin is +ve distance
  // If k is 0, then plane through origin & the order of p,q,r
  // determine +ve direction (how?)
  if (k > 0.0) { k = -k; n = -n; }
  return vec4(n,k);
}

