float scene(vec3 pos) {
  int flips = 0;
  pos = fold(pos,flips,m);
  uv = pos.xy;
  int parity = flips%2;
  float t = iTime;
  vec3 n1,n2;
  float d1 = faces(pos,int(t)/4,n1);
  float d2 = faces(pos,(int(t)+1)/4,n2);
  float d = mix(d1,d2,fract(t));
  n = mix(n1,n2,fract(t));
  n = m*n; // Transform to world coordinates
  normalize(n); // Keep normalized!
  if (doclip && pos0.x > d) {
    d = pos0.x;
    n = vec3(1,0,0);
  }
  return d;
}
#endif

void mindistance(float d, int t, inout float dmin, inout int type) {
  if (d < dmin) { dmin = d; type = t; }
}
void maxdistance(float d, inout float dmax) {
  if (d > dmax) { dmax = d; }
}
void triangle(vec3 p0, vec3 p, vec3 q, vec3 r, float radius, int t, inout float dmin, inout int type) {
  mindistance(segment(p0,p,q)-radius,t,dmin,type);
  mindistance(segment(p0,q,r)-radius,t,dmin,type);
  mindistance(segment(p0,r,p)-radius,t,dmin,type);
}

float cradius = 0.1; // Sphere at origin
float pradius = 0.05; // Sphere at main vertex
float rradius = 0.03; // Sphere at other region vertices
float sradius = 0.01;

// The achiral scene.
float scene1(vec3 p, inout int type, inout float d) {
  vec3 a1 = a*dot(a,c);
  vec3 b1 = b*dot(b,c);
  vec3 c1 = c;
  vec3 O = vec3(0);

  // Always draw the dodecahedron vertices
    float k = 1.0 + pradius;
  mindistance(length(p-k*c) - pradius,0,d,type);
#if 1
  if (true) {
    // Dodecahedron edges
    mindistance(segment(p,k*c,reflect(k*c,C))-0.03,1,d,type);
  }
  if (false) {
    // Fundamental region
    mindistance(length(p) - cradius,0,d,type);
    mindistance(length(p-a1) - rradius,0,d,type);
    mindistance(length(p-b1) - rradius,0,d,type);
    mindistance(length(p-c1) - rradius,0,d,type);

    mindistance(segment(p,c,a1)-sradius,3,d,type);
    mindistance(segment(p,c,b1)-sradius,3,d,type);
    mindistance(segment(p,a1,b1)-sradius,3,d,type);
    mindistance(segment(p,O,a1)-sradius,3,d,type);
    mindistance(segment(p,O,b1)-sradius,3,d,type);
    mindistance(segment(p,O,c)-sradius,3,d,type);
  }
  //int M = 1+int(iTime)%18;
  //mindistance(segment(p,vertices[0],vertices[M])-0.0301,2,d,type);
  if (false) {
    // compound of cubes
    mindistance(segment(p,vertices[1],vertices[3])-0.0301,2,d,type);
    mindistance(segment(p,vertices[0],vertices[4])-0.0301,2,d,type);
  }
#endif
  return d;
}

// The chiral scene. There is only one set of planes that can be split
// up into coherent chiral parts, that in fact form the compound of
// five tetrahedra.
float scene0(vec3 p, inout int type, inout float d) {
  if (true) {
    //mindistance(segment(p,vertices[0],vertices[10])-0.0301,2,d,type);
    //mindistance(segment(p,vertices[3],vertices[5])-0.0301,2,d,type);
  }
  float d1 = -1e8;
  maxdistance(dot(vec4(p,1),makeplane(vertices[0],vertices[14],vertices[10])),d1);
  maxdistance(dot(vec4(p,1),makeplane(vertices[0],vertices[12],vertices[10])),d1);
  //maxdistance(dot(vec4(p,1),makeplane(vertices[0],vertices[5],vertices[int(iTime)%20])),d1);
 if (d1 < d) { d = d1; type = 3; }
  return d;
}

float scene(vec3 p0, out vec2 uv, out int type) {
  type = 0;
  int flips = 0;
  float d = 1e8;
  vec3 p = fold(p0,flips);
  uv = p.xy;
  if (flips%2 == 0) {
    scene0(p,type,d);
  } else {
    scene0(reflect(p,A),type,d);
    scene0(reflect(p,B),type,d);
    scene0(reflect(p,C),type,d);
  }
  scene1(p,type,d);
  assert(type >= 0 && type <= 3);
  if (doclip && p0.x > d) {
    d = p0.x;
  }
  //if (type == 0 && distance(p0,c) < 0.2) type = 4;
  //return max(d,dot(p,b)-dot(b,c)); // Clip to dodecahedron
  return d;
}

