void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates for y (from -1 to 1), for x (from -xMin to xMax)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // bg color
    vec3 col = vec3(.3);
    // rotating speed
    float speed = 2.5;
    // rotation matrix
    float angle = radians(iTime*speed);
    float sa = sin(angle);
    float ca = cos(angle);
    mat2x2 rot = mat2x2(ca, -sa, sa, ca);
    // capsule
    float l = .4;
  	vec2 p1 = vec2(-l/2., 0);
    vec2 p2 = vec2(l/2., 0);
    for (float i = 0.; i < 9.; i++) {
        // rotate capsule
        p1 = rot * p1;
        p2 = rot * p2;

        // distance from line
        float t = dot(uv-p1, p2-p1)/(length(p2-p1)*length(p2-p1));
        t = clamp(t, 0., 1.);
        vec2 p = mix(p1, p2, t);
        float d = length(uv-p);
        col.r += step(.2, d)*0.27;
        // swap colors
        col.rgb = col.gbr;
    }

    // Output to screen
    fragColor = vec4(col, 1);
}
