void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0.0);
    vec2 pos = vec2(0.5)-uv;
    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float f = cos(a*3.);
    f = abs(cos(a*12.)*sin(a*3.))*.8+.1;
    color = vec3( 1.-smoothstep(f,f+0.02,r) );

    uv -= vec2(0.5);
    // uv = scale( vec2(sin(u_time)+1.0) ) * uv;

    // uv = rotate2d( sin(u_time)*PI ) * uv;

    uv += vec2(0.5);
    color += vec3(uv,0.2);

    // Time varying pixel color
     vec3 col = 0.6 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
     vec3 sol = 0.4 + 0.4*sin(iTime+uv.xyx+vec3(0,2,4));
     vec3 tol = 0.5 + 0.6*tan(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(color - col + sol * tol, tol);
}
