#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float add_Plane( vec3 p )
{
	return p.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = ((fragCoord)-iResolution.xy)/iResolution.y+1.0;
    vec3 col =  normalize( vec3(p,cos(iTime)));
    float a = add_Plane(col);
    // Time varying pixel color
    vec3 col2 = normalize(vec3(a*cos(iTime)/iTime*a-col));
    vec3 col3 = normalize(vec3(col*a/cos(iTime+col2)));
    // Output to screen
    fragColor = vec4(col3,1.0);
}
