void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uva = fragCoord/iResolution.xy;
    //uva.x *= iResolution.x/iResolution.y;
    uva.x += texture(iChannel1,uv).r;
    uva.y += texture(iChannel1,uv).g;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uva).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(sin(iTime)*cos(uv.x*0.5),sin(iTime*0.5)*sin(uv.x*0.75),0.0,1.0);
}
