const float c_pi = 3.14159265359f;
const float c_twopi = 2.0f * c_pi;


uint wang_hash(inout uint seed)
{
    seed = uint(seed ^ uint(61)) ^ uint(seed >> uint(16));
    seed *= uint(9);
    seed = seed ^ (seed >> 4);
    seed *= uint(0x27d4eb2d);
    seed = seed ^ (seed >> 15);
    return seed;
}

float RandomFloat01(inout uint state)
{
    return float(wang_hash(state)) / 4294967296.0;
}

vec3 RandomUnitVector(inout uint state)
{
    float z = RandomFloat01(state) * 2.0f - 1.0f;
    float a = RandomFloat01(state) * c_twopi;
    float r = sqrt(1.0f - z * z);
    float x = r * cos(a);
    float y = r * sin(a);
    return vec3(x, y, z);
}



float hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

vec3 uniformVector( in float seed)
{
    float a = 3.141593*hash( 78.233 + seed);
    float b = 6.283185*hash( 10.873 + seed);
    return vec3( sin(b)*sin(a), cos(b)*sin(a), cos(a) );
}

vec3 cosineDirection( in float seed, in vec3 nor)
{
    float u = hash( 78.233 + seed);
    float v = hash( 10.873 + seed);


    	float ks = (nor.z>=0.0)?1.0:-1.0;     //do not use sign(nor.z), it can produce 0.0
        float ka = 1.0 / (1.0 + abs(nor.z));
        float kb = -ks * nor.x * nor.y * ka;
        vec3 uu = vec3(1.0 - nor.x * nor.x * ka, ks*kb, -ks*nor.x);
        vec3 vv = vec3(kb, ks - nor.y * nor.y * ka * ks, -nor.y);

        float a = 6.2831853 * v;
        return sqrt(u)*(cos(a)*uu + sin(a)*vv) + sqrt(1.0-u)*nor;
}



float intersect(in vec3 ro, in vec3 rd) {//normal sphere tracing function
	float res = -1.0;
    float tmax = 16.0;
    float t = 0.001;
    for(int i = 0; i < 128; i++) {
    	float h = map(ro+rd*t).x;
        if((h<0.0001) || (t>tmax)) break;
        t += h;

    }
    if(t<tmax) res = t;

    return res;

}


float antiintersect(in vec3 ro, in vec3 rd) {//inverse sphere tracing function, sphere traces the inside of transparent objects
	float res = -1.0;
    float tmax = 16.0;
    float t = 0.001;
    for(int i = 0; i < 128; i++) {
    	float h = map2(ro+rd*t).x;
        if((h<0.0001) || (t>tmax)) break;
        t += h;

    }
    if(t<tmax) res = t;

    return res;

}





vec3 calcFinalColor(vec3 ro, vec3 rd, float sa) {//main function
    vec3 accumulatedColor = vec3(0.0);
        vec3 colorMask = vec3(1.0);
            float fdis = 0.0;


    for(int bounce = 0; bounce < 5; bounce++) {
        float emmis = 0.0;
        float refl = 0.0;
        float refr = 0.0;
        float t = intersect(ro, rd);
        if( t < 0.0 )
        {

            fdis=16.0; 
            accumulatedColor += vec3(texture(iChannel0, rd).rgb);

            break;
        }
    	if(bounce == 0) fdis = t;
        vec3 pos = ro + rd*t;
        vec3 surfaceColor = GetMatProps(map(pos).y, refl, refr, emmis);;
        vec3 norm = calcNormal(pos);
        vec3 iColor = vec3(0.0);
		float rdist = 0.0;
        if(refr == 1.0) {//if material is refractive
            		float eps = 1.5;
            float rand = 76.2 + 73.1*float(bounce) + sa + 17.7*float(iFrame);
            if(t < 0.01) {//theory: if ray is already inside an object, use first calculation, else use second calculation
                norm = -norm;

            }else {
               eps = 1.0/eps;
            }
            float fres = fresnel(rd, norm, 1.0/eps);
            if(fres > rand-floor(rand)){
            rd = reflect(rd, norm);  


            }else{


                vec3 refractResult = refract(rd, norm, eps);
                        if (length(refractResult) > 0.0001) {
                            rd = refractResult;
                        }
            }

            ro = pos;

        t = antiintersect(ro, rd);
        pos = ro + rd*t;
 		rdist = length(ro-pos);//get distance for use in color calculation

        ro = pos;


            colorMask *= exp(-(surfaceColor*0.3) * rdist * rdist * rdist); //coloring based on distance ray travels through transparent object
        }else { //else if material not refractive
                  colorMask *= surfaceColor;
				float isDif = 1.0-refl;

            //mix between reflective and diffuse ray directions
            rd = mix( normalize(reflect(rd, norm)), cosineDirection(76.2 + 73.1*float(bounce) + sa + 17.7*float(iFrame), norm), isDif);


            ro = pos;

              }


        iColor += (emmis * surfaceColor) + 2.0 * accumulatedColor * dot(norm, rd);//apply colors and emmisivity
        accumulatedColor += colorMask * iColor;





    }



        float ff = exp(-0.01*fdis*fdis);
    accumulatedColor *= ff; 
    accumulatedColor += (1.0-ff)*0.05*vec3(0.9,1.0,1.0);



    return accumulatedColor;
}




